$.fn.loadImage = function (src) {
	var i = $(this);
	if (i.attr('src') == src) {
		return i;
	}
	i.attr('src', WbsData.get('url.common', '/common/') + 'img/loading.gif');
	var img = $('<img src="' + src + '" />');
	img.load(function () {
		i.attr('src', $(this).attr('src'));
		
	});
	return i;
}

